import React from 'react'

const Header = (course) =>{
  return(
    <>
    <h1>{course.name}</h1>
    </>)
}
const Part = (part) =>{
  return(
    <>
      <p>
        {part.part} {part.exercises}
      </p>
    </>
  )
}
const Content = (parts) =>{
  const [part1,part2,part3] = parts
  console.log(parts)
  return(
    <>
      <Part part = {part1.name} exercises = {part1[0].exercises} />
      <Part part = {part2[1].name} exercises = {part2[1].exercises} />
      <Part part = {part3[2].name} exercises = {part3[2].exercises} />
    </>
  )
}
const Total = (props) =>{
  return(
    <>
    <p>
      Number of exercises {props[0].exercises+props[1].exercises+props[2].exercises}
    </p>
    </>
  )

}
const App = () => {
  const course = {
    name: 'Half Stack application development',
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10
      },
      {
        name: 'Using props to pass data',
        exercises: 7
      },
      {
        name: 'State of a component',
        exercises: 14
      }
    ]
  }
  return (
    <div>
      <Header name={course.name} />
      <Content content={course.parts}/>
      <Total  num = {course.parts}/>
    </div>
  )
}

export default App
