  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
        <Header name={course} />
        <Content parts={part1} />
        <Content parts={part2} />
        <Content parts={part3} />
        <Total num={part1,part2,part3} />
    </div>
  )
}


