import React, { useState } from 'react'


const Display = props => <div>{props.text} {props.value}</div>

const Statistics = (props) =>{
  return (
    <>
          <Display text='good' value={props.good}/>
          <Display text='netural' value={props.neutral}/>
          <Display text='bad' value={props.bad}/>
          <Display text='all' value={props.good+props.neutral+props.bad}/>
          <Display text='average' value={(props.good-props.bad)/(props.good+props.neutral+props.bad)}/>
          <Display text='positive' value={((props.good / (props.good + props.neutral + props.bad)) * 100)}/>

    </>
  )
}
const App = () => {
  // save clicks of each button to own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const handGoodClick = () => {
    setGood(good + 1)
  }

  const handleNeturalClick = () => {
    setNeutral(neutral + 1)
  }
  const handleBadClick = () => {
    setBad(bad + 1)
  }

  return (
    <div>
      <h1>give feedback</h1>
      <Button handleClick={handGoodClick} text='good'/>
      <Button handleClick={handleNeturalClick} text='neutral'/>
      <Button handleClick={handleBadClick} text='bad'/>
      <h1>statistics</h1>
      <Statistics good={good} bad={bad} neutral={neutral} />
    </div>
  )
}

const Button = ({handleClick, text}) => 
  (
    <button onClick={handleClick}>
      {text}
    </button>
  )


export default App
